import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import axios from 'axios';

interface SearchResult {
  id: string;
  malId?: number;
  title: {
    romaji?: string;
    english?: string;
    native?: string;
  };
  status?: string;
  image?: string;
  cover?: string;
  popularity?: number;
  description?: string;
  rating?: number;
  genres?: string[];
  color?: string;
  totalChapters?: number;
  volumes?: number;
  type?: string;
  releaseDate?: string;
}

const Search: React.FC = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [searchResults, setSearchResults] = useState<SearchResult[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const searchQuery = new URLSearchParams(location.search).get('q');
    if (searchQuery) {
      fetchSearchResults(searchQuery);
    }
  }, [location.search]);

  const fetchSearchResults = async (query: string) => {
    setIsLoading(true);
    setError(null);

    try {
      const response = await axios.get(`https://api.consumet.org/meta/anilist-manga/${encodeURIComponent(query)}`);
      const results = response.data.results.map((result: any) => ({
        id: result.id,
        malId: result.malId,
        title: result.title || {},
        status: result.status,
        image: result.image,
        cover: result.cover,
        popularity: result.popularity,
        description: result.description,
        rating: result.rating,
        genres: result.genres || [],
        color: result.color,
        totalChapters: result.totalChapters,
        volumes: result.volumes,
        type: result.type,
        releaseDate: result.releaseDate,
      }));
      setSearchResults(results);
    } catch (error) {
      setError('An error occurred while fetching search results.');
    }

    setIsLoading(false);
  };

  const handleViewDetails = (id: string) => {
    navigate(`/manga/${id}`);
  };

  const truncateDescription = (description: string | undefined, maxLength: number) => {
    if (!description) {
      return '';
    }
    if (description.length <= maxLength) {
      return description;
    }
    return description.slice(0, maxLength) + '...';
  };

  return (
    <Container>
      <h1>Search Results</h1>
      {isLoading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>{error}</p>
      ) : searchResults.length > 0 ? (
        <Row>
          {searchResults.map((result) => (
            <Col md={4} key={result.id}>
              <Card className="mb-4">
                <div style={{ position: 'relative' }}>
                  {result.image && (
                    <Card.Img
                      variant="top"
                      src={result.image}
                      style={{ height: '300px', objectFit: 'cover' }}
                    />
                  )}
                  <div
                    style={{
                      position: 'absolute',
                      bottom: 0,
                      left: 0,
                      right: 0,
                      backgroundColor: 'rgba(0, 0, 0, 0.6)',
                      padding: '10px',
                      color: 'white',
                    }}
                  >
                    <Card.Title>{result.title.english || result.title.romaji || result.title.native}</Card.Title>
                  </div>
                </div>
                <Card.Body>
                  <Card.Text>{truncateDescription(result.description, 100)}</Card.Text>
                  <Button variant="primary" onClick={() => handleViewDetails(result.id)}>
                    View Details
                  </Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      ) : (
        <p>No search results found.</p>
      )}
    </Container>
  );
};

export default Search;
