import React, { useEffect, useState, useRef } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { Container, Row, Col, Card, Button, Image, Overlay, OverlayTrigger, Tooltip } from 'react-bootstrap';
import axios from 'axios';
import './Search.css';

interface SearchResult {
  id: string;
  title: string;
  altTitles: string[];
  image: string;
  description: string;
  genres: string[];
  status: string;
  rating: number | null;
}

const Search: React.FC = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [searchResults, setSearchResults] = useState<SearchResult[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [expandedImageId, setExpandedImageId] = useState<string | null>(null);

  const imageRefs = useRef<{ [key: string]: HTMLDivElement | null }>({});

  useEffect(() => {
    const searchQuery = new URLSearchParams(location.search).get('q');
    if (searchQuery) {
      fetchSearchResults(searchQuery);
    }
  }, [location.search]);

  const fetchSearchResults = async (query: string) => {
    setIsLoading(true);
    setError(null);

    try {
      const response: { data: { results: any[] } } = await axios.get(
        `https://consumet-api-z0sh.onrender.com/meta/anilist-manga/${encodeURIComponent(query)}`
      );

      const results: SearchResult[] = response.data.results
        .filter((result: any) => !result.genres.includes('Ecchi'))
        .map((result: any) => ({
          id: result.id,
          title: result.title.userPreferred,
          altTitles: [result.title.romaji, result.title.english, result.title.native].filter(Boolean),
          image: result.image,
          description: result.description || '',
          genres: result.genres,
          status: result.status,
          rating: result.rating,
        }));

      setSearchResults(results);
    } catch (error) {
      setError('An error occurred while fetching search results.');
      console.error('Error fetching search results:', error);
    }

    setIsLoading(false);
  };

  const handleViewDetails = (id: string) => {
    navigate(`/manga/${id}`);
  };

  const handleImageExpand = (id: string | null) => {
    setExpandedImageId(id);
  };

  return (
    <Container>
      <h1>Search Results</h1>
      {isLoading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>{error}</p>
      ) : searchResults.length > 0 ? (
        <Row>
          {searchResults.map((result) => (
            <Col md={4} key={result.id}>
              <Card className="search-card">
                <div
                  ref={(node) => (imageRefs.current[result.id] = node)}
                  className="search-card-image-container"
                  onClick={() => handleImageExpand(result.id)}
                >
                  <Image src={result.image} alt={result.title} className="search-card-image" />
                  <div className="search-card-title-panel">
                    <Card.Title>{result.title}</Card.Title>
                  </div>
                </div>
                <Card.Body>
                  <Card.Text>
                    {result.description ? result.description.slice(0, 100) + '...' : 'No description available.'}
                  </Card.Text>
                  <Card.Text>Rating: {result.rating || 'N/A'}</Card.Text>
                  <Card.Text>Genres: {result.genres.join(', ')}</Card.Text>
                  <Button variant="primary" onClick={() => handleViewDetails(result.id)}>
                    View Details
                  </Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      ) : (
        <p>No search results found.</p>
      )}
    </Container>
  );
};

export default Search;
