import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { Container, Row, Col, Card, Button, Image as BSImage } from 'react-bootstrap';
import axios from 'axios';

interface SearchResult {
  id: string;
  title: string;
  altTitles: string[];
  headerForImage: { Referer: string };
  image: string;
  description: string;
}

const Search: React.FC = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [searchResults, setSearchResults] = useState<SearchResult[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const searchQuery = new URLSearchParams(location.search).get('q');
    if (searchQuery) {
      fetchSearchResults(searchQuery);
    }
  }, [location.search]);

  const fetchSearchResults = async (query: string) => {
    setIsLoading(true);
    setError(null);
  
    try {
      const response = await axios.get(`https://consumet-api-z0sh.onrender.com/manga/mangadex/${encodeURIComponent(query)}`);
      const results = response.data.results 
      .map((result: any) => ({
        id: result.id,
        title: result.title,
        altTitles: result.altTitles ? result.altTitles.map((title: any) => title.en || title.ja) : [],
        headerForImage: result.headerForImage,
        image: result.image,
        description: result.description || '', // Provide a default value if description is undefined
      }));
      setSearchResults(results);
    } catch (error) {
      setError('An error occurred while fetching search results.');
    }
  
    setIsLoading(false);
  };

  const handleViewDetails = (id: string) => {
    navigate(`/manga/${id}`);
  };

  const truncateDescription = (description: string | undefined, maxLength: number) => {
    if (!description) {
      return '';
    }
    if (description.length <= maxLength) {
      return description;
    }
    return description.slice(0, maxLength) + '...';
  };

  return (
    <Container>
      <h1>Search Results</h1>
      {isLoading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>{error}</p>
      ) : searchResults.length > 0 ? (
        <Row>
          {searchResults.map((result) => (
            <Col md={4} key={result.id}>
              <Card className="mb-4">
                <Card.Body>
                  <BSImage 
                    src={result.image}
                    alt={result.title}
                    fluid
                    style={{ maxHeight: '300px', objectFit: 'contain' }}
                  />
                  <Card.Title>{result.title}</Card.Title>
                  <Card.Subtitle className="mb-2">
                    {result.altTitles.join(', ')}
                  </Card.Subtitle>
                  <Button variant="primary" onClick={() => handleViewDetails(result.id)}>
                    View Details
                  </Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      ) : (
        <p>No search results found.</p>
      )}
    </Container>
  );
};

export default Search;
