import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';

interface SearchResult {
  id: string;
  title: string;
  description: string;
  coverImage: string;
}

const Search: React.FC = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [searchResults, setSearchResults] = useState<SearchResult[]>([]);

  useEffect(() => {
    const searchQuery = new URLSearchParams(location.search).get('q');
    const resultsFromState = location.state?.results;
    if (searchQuery && resultsFromState) {
      setSearchResults(resultsFromState);
    }
  }, [location.search, location.state]);

  const handleViewDetails = (id: string) => {
    navigate(`/novel/${id}`);
  };

  return (
    <Container>
      <h1>Search Results</h1>
      {searchResults.length > 0 ? (
        <Row>
          {searchResults.map((result) => (
            <Col md={4} key={result.id}>
              <Card className="mb-4">
                <Card.Img variant="top" src={result.coverImage} />
                <Card.Body>
                  <Card.Title>{result.title}</Card.Title>
                  <Card.Text>{result.description}</Card.Text>
                  <Button variant="primary" onClick={() => handleViewDetails(result.id)}>
                    View Details
                  </Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      ) : (
        <p>No search results found.</p>
      )}
    </Container>
  );
};

export default Search;
